import styles from './AvailableErrors.module.css';
import ErrorItem from './ErrorItem/ErrorItem';
import Card from '../UI/Card'
import Timer from './ErrorItem/timer';

let today = new Date()

export const DUMMY_ERRORS = [
    {
      id: 'm1',
      name: 'CH1_RAFTOR_56',
      description: 'Ping Down',
      time: today.getHours() + ':' + today.getMinutes() + ':' + today.getSeconds(),
      state: 1
    },
    // {
    //   id: 'm2',
    //   name: 'CH1_RAFTOR_78',
    //   description: 'link not in dispatch',
    //   time: today.getHours() + ':' + today.getMinutes() + ':' + today.getSeconds(),
    // },
    // {
    //   id: 'm3',
    //   name: 'CH1_RAFTOR_34',
    //   description: 'Link GPS Out Of Sync',
    //   time: today.getHours() + ':' + today.getMinutes() + ':' + today.getSeconds(),
    // },
    // {
    //   id: 'm4',
    //   name: 'CH2_RAFTOR_34',
    //   description: 'NLM Link Is Down',
    //   time: today.getHours() + ':' + today.getMinutes() + ':' + today.getSeconds(),
    // },
  ];



const AvilableErrors = (props) => {
    const errorsList = DUMMY_ERRORS.map((error) => 
        <ErrorItem key={error.id} name={error.name} description={error.description} time={<Timer/>} />
    );
    return <section className={styles.errors}>
        <Card>
            <ul>
                {errorsList}
            </ul>
        </Card>
    </section>
};


export default AvilableErrors
