const AvilableErrors = (props) => {
    const errorsList = DUMMY_ERRORS.map((error) => 
        <ErrorItem key={error.id} name={error.name} description={error.description} time={<Timer/>} addErrorHandler={props.addErrorHandler}/>
    );
    return <section className={styles.errors}>
        <Card>
            <ul>
                {errorsList}
            </ul>
        </Card>
    </section>
};


export default AvilableErrors
