import React, {useState} from "react";
import Detect from "./components/Layout/Detect";
import Header from "./components/Layout/Header";
// import { DUMMY_ERRORS } from "./components/Errors/AvilableErrors";
function App() 
{
  const [errorListInTreatment, setErrorsList] = useState([]);
  const [errorListInFinish, setErrorsTreatmentList] = useState([]);
  const [DUMMY_ERRORS, setDUMMYERRORS] = useState([
    {
      id: 'm1',
      name: 'CH1_RAFTOR_56',
      description: 'Ping Down',
      time: '20:00:00',
      state: 1
    },
    {
      id: 'm2',
      name: 'CH1_RAFTOR_78',
      description: 'link not in dispatch',
      time: '20:00:00',
    },
    {
      id: 'm3',
      name: 'CH1_RAFTOR_34',
      description: 'Link GPS Out Of Sync',
      time: '20:00:00',
    },
    {
      id: 'm4',
      name: 'CH2_RAFTOR_34',
      description: 'NLM Link Is Down',
      time: '20:00:00',
    },
   ]);

const deleteItemHandler = errorId => {
  setDUMMYERRORS(DUMMY_ERRORS.filter(element => element.id !== errorId));
  console.log(DUMMY_ERRORS)
};


  const addErrorHandler = (errorName, errorDescription, errorTime) => {
    setErrorsList((prevUsersList) => {
      return [...prevUsersList, {name: errorName, description: errorDescription, time: errorTime ,id: Math.random().toString()}];
    })
  }


  const addErrorFinishHandler = (errorName, errorDescription, errorTime) => {
    setErrorsTreatmentList((prevUsersList) => {
      return [...prevUsersList, {name: errorName, description: errorDescription, time: errorTime ,id: Math.random().toString()}];
    })
  }

  return (
    <>
    <Header color={'#A459D1'}/>
    <Detect color={'#E9A178'} id={1} errorListInTreatment={errorListInTreatment} errorList={DUMMY_ERRORS} addErrorHandler={addErrorHandler} addErrorFinishHandler={addErrorFinishHandler} deleteError={deleteItemHandler}/>
    <Detect color={'#F7C04A'} id={2} errorListInTreatment={errorListInTreatment} errorList={DUMMY_ERRORS} addErrorHandler={addErrorHandler} addErrorFinishHandler={addErrorFinishHandler} deleteError={deleteItemHandler}/>
    <Detect color={'#609966'} id={3} errorListInTreatment={errorListInTreatment} errorList={DUMMY_ERRORS} addErrorHandler={addErrorHandler} addErrorFinishHandler={addErrorFinishHandler} deleteError={deleteItemHandler}/>
    </>
  );
}

export default App;
