import React, {useState} from "react";
import Detect from "./components/Layout/Detect";
import Header from "./components/Layout/Header";
// import { DUMMY_ERRORS } from "./components/Errors/AvilableErrors";
function App() 
{
  const [errorListInTreatment, setErrorsList] = useState([]);
  const [errorListInFinish, setErrorsTreatmentList] = useState([]);


  const addErrorHandler = (errorName, errorDescription, errorTime) => {
    setErrorsList((prevUsersList) => {
      return [...prevUsersList, {name: errorName, description: errorDescription, time: errorTime ,id: Math.random().toString()}];
    })
  }

  const addErrorFinishHandler = (errorName, errorDescription, errorTime) => {
    setErrorsTreatmentList((prevUsersList) => {
      return [...prevUsersList, {name: errorName, description: errorDescription, time: errorTime ,id: Math.random().toString()}];
    })
  }

  return (
    <>
    <Header color={'#A459D1'}/>
    <Detect color={'#E9A178'} id={Math.random().toString()} errorsInTreatment={errorListInTreatment}/>
    <Detect color={'#F7C04A'} id={Math.random().toString()} errorsInTreatment={errorListInTreatment} />
    <Detect color={'#609966'} id={Math.random().toString()} errorsInTreatment={errorListInTreatment}/>
    </>
  );
}

export default App;
